CREATE PROCEDURE proc7 (IN product_id INT, OUT type CHAR(10))BEGIN  DECLARE prod_name CHAR(20) ;  SELECT name INTO prod_name FROM "GROUPO"."Products"  WHERE ID = product_id;  CASE prod_name  WHEN 'Tee Shirt' THEN    SET type = 'Shirt'  WHEN 'Sweatshirt' THEN    SET type = 'Shirt'  WHEN 'Baseball Cap' THEN    SET type = 'Hat'  WHEN 'Visor' THEN    SET type = 'Hat'  WHEN 'Shorts' THEN    SET type = 'Shorts'  ELSE    SET type = 'UNKNOWN'  END CASE ;END